import React, { useState, useEffect } from 'react';
import Search from './components/Search.js';
import Form from './components/Form.js';
import Phonebook from './components/Phonebook.js';
import personService from './services/persons.js';
const App = () => {
	const [ NewName, setNewName ] = useState('');
	const [ NewNumber, setNewNumber ] = useState('');
	const [ Query, setQuery ] = useState('');
	const [ Persons, setPersons ] = useState([]);
	//===========================================================================
	useEffect(() => {
		async function fetchData() {
			const initialPersons = await personService.getAll();
			setPersons(initialPersons);
		}
		fetchData();
	}, []);

	//===========================================================================

	const handleNameChange = (event) => {
		setNewName((event.target.name = event.target.value));
	};

	const handleQueryChange = (event) => {
		setQuery((event.target.search = event.target.value));
	};

	const handleNumberChange = (event) => {
		setNewNumber((event.target.number = event.target.value));
	};

	//===========================================================================

	const addName = async (event) => {
		event.preventDefault();
		if (NewName !== '' && NewNumber !== '') {
			const newObject = {
				name: NewName,
				number: NewNumber,
				id: Persons.length + 1
			};

			// console.log(Persons.map((p) => p.name));
			Persons.filter((p) => {
				return p.name === NewName || p.number === NewNumber;
			})
				? console.log('One of the items is true')
				: console.log('None of the thing is true');

			const data = await personService.create(newObject);
			setPersons(Persons.concat(data));
		}
		setNewNumber('');
		setNewName('');
	};

	return (
		<div>
			<h1>Phonebook</h1>
			<Search handleQueryChange={handleQueryChange} Query={Query} />
			<br />
			<br />
			<Form
				handleNameChange={handleNameChange}
				handleNumberChange={handleNumberChange}
				NewName={NewName}
				NewNumber={NewNumber}
				addName={addName}
			/>
			<Phonebook Persons={Persons} Query={Query} NewName={NewName} NewNumber={NewNumber} />
		</div>
	);
};

export default App;
