import React from 'react';

const Header = ({ courses }) => {
	return <h1>{courses.map((course) => course.name)}</h1>;
};

const Total = ({ courses }) => {
	//const sum = course.parts.map((part) => part.exercises).reduce((a, b) => a + b);
	const sum = courses.map((course) =>
		course.parts.map((part) => part.exercises).reduce((a, b) => a + b)
	);
	return <p>Number of exercises {sum}</p>;
};

const Part = (props) => {
	return (
		<p>
			{props.part.name} {props.part.exercises}
		</p>
	);
};

const Content = ({ course }) => {
	return <div>{course.parts.map((part) => <Part part={part} />)}</div>;
};

const Course = (props) => {
	return (
		<div>
			<Header course={props.course} />
			<Content course={props.course} />
			<Total course={props.course} />
		</div>
	);
};

const App = () => {
	const courses = [
		{
			name: 'Half Stack application development',
			parts: [
				{
					name: 'Fundamentals of React',
					exercises: 10
				},
				{
					name: 'Using props to pass data',
					exercises: 7
				},
				{
					name: 'State of a component',
					exercises: 14
				},
				{
					name: 'Redux',
					exercises: 11
				}
			]
		}
	];

	return (
		<div>
			<Course courses={courses} />
		</div>
	);
};

export default App;
