import React, { useState } from 'react';
import Search from './components/Search.js';
import Form from './components/Form.js';
const App = () => {
	const [ Persons, setPersons ] = useState([
		{ name: 'Bashir Sidi', number: '08103510117' },
		{ name: 'Ahmad Sidi', number: '08067753836' }
	]);
	const [ NewName, setNewName ] = useState('');
	const [ NewNumber, setNewNumber ] = useState('');
	const [ Query, setQuery ] = useState('');

	const handleNameChange = (event) => {
		setNewName((event.target.name = event.target.value));
	};

	const handleQueryChange = (event) => {
		setQuery((event.target.search = event.target.value));
	};

	const handleNumberChange = (event) => {
		setNewNumber((event.target.number = event.target.value));
	};

	const addName = (event) => {
		event.preventDefault();
		if (NewName !== '' && NewNumber !== '') {
			var newPersons = [ ...Persons, { name: NewName, number: NewNumber } ];
			setPersons(newPersons);
		}
		setNewNumber('');
		setNewName('');
	};

	return (
		<div>
			<h1>Phonebook</h1>
			<Search handleQueryChange={handleQueryChange} Query={Query} />
			<br />
			<br />
			<Form
				handleNameChange={handleNameChange}
				handleNumberChange={handleNumberChange}
				NewName={NewName}
				NewNumber={NewNumber}
				addName={addName}
			/>
			<h2>Numbers</h2>
			{console.log(Persons.map((person) => person))}
			<div>
				{Query !== '' ? (
					Persons.filter((person) => person.name === Query).map((filteredPerson, i) => (
						<div key={i}>
							{filteredPerson.name}: {filteredPerson.number}
						</div>
					))
				) : (
					<div>
						{Persons.map(
							(person, i) =>
								person.name !== NewName ? (
									<div key={i}>
										{person.name}: {person.number}
									</div>
								) : (
									alert(`${NewName} is already added to Phonebook!`)
								)
						)}
					</div>
				)}
			</div>
		</div>
	);
};

export default App;
