import React, { useState } from 'react';
const App = () => {
	const [ Persons, setPersons ] = useState([
		{ name: 'Bashir Sidi', number: '08103510117' },
		{ name: 'Ahmad Sidi', number: '08067753836' }
	]);
	const [ NewName, setNewName ] = useState('');
	const [ NewNumber, setNewNumber ] = useState('');

	const handleChange = (event) => {
		setNewName(([ event.target.name[name] ] = event.target.value));
		setNewNumber(([ event.target.name[number] ] = event.target.value));
	};

	const addName = (event) => {
		event.preventDefault();
		if (NewName !== '' && NewNumber !== '') {
			var newPersons = [ ...Persons, { name: NewName, number: NewNumber } ];
			setPersons(newPersons);
		}
		setNewNumber('');
		setNewName('');
	};
	return (
		<div>
			<h1>Phonebook</h1>
			<form onSubmit={addName}>
				<div>
					Name: <input name="name" value={NewName} onChange={handleChange} />
					&nbsp;&nbsp;Number:{' '}
					<input name="number" value={NewNumber} onChange={handleChange} />
					<br />
					<br />
					<button type="submit">Add</button>
				</div>
			</form>
			<h2>Numbers</h2>
			{console.log(Persons.map((person) => person))}
			{Persons.map(
				(person, i) =>
					person.name !== NewName ? (
						<div key={i}>
							{person.name}: {person.number}
						</div>
					) : (
						alert(`${NewName} is already added to Phonebook!`)
					)
			)}
		</div>
	);
};

export default App;
