import React from 'react';
import { useState } from 'react';

const Anecdote = ({ text, votes = 0 }) => {
	return (
		<div>
			<div>{text}</div>
			<div>has {votes} votes</div>
		</div>
	);
};

const App = () => {
	const anecdotes = [
		'If it hurts, do it more often',
		'Adding manpower to a late software project makes it later!',
		'The first 90 percent of the code accounts for the first 90 percent of the development time...The remaining 10 percent of the code accounts for the other 90 percent of the development time.',
		'Any fool can write code that a computer can understand. Good programmers write code that humans can understand.',
		'Premature optimization is the root of all evil.',
		'Debugging is twice as hard as writing the code in the first place. Therefore, if you write the code as cleverly as possible, you are, by definition, not smart enough to debug it.'
	];
	const [ selected, setSelected ] = useState(0);
	const [ votes, setVotes ] = useState({});
	const [ mostVotes, setMostVotes ] = useState(0);

	const next = () => {
		let random = Math.floor(Math.random() * anecdotes.length);
		while (random === selected) {
			random = Math.floor(Math.random() * anecdotes.length);
		}

		setSelected(random);
	};
	console.log(votes);
	const vote = () => {
		const selectedVoteCount = votes[selected] || 0;
		setVotes({
			...votes,
			[selected]: selectedVoteCount + 1
		});
		if (!votes[mostVotes] || selectedVoteCount + 1 > votes[mostVotes]) {
			setMostVotes(selected);
		}
	};

	return (
		<div>
			<h2>Anecdote of the day</h2>
			<Anecdote text={anecdotes[selected]} votes={votes[selected]} />
			<button onClick={vote}>vote</button>
			<button onClick={next}>next anecdote</button>
			<h2>Anecdote with most votes</h2>
			<Anecdote text={anecdotes[mostVotes]} votes={votes[mostVotes]} />
		</div>
	);
};

export default App;
