import React, { useState } from 'react';
const App = () => {
	const [ Persons, setPersons ] = useState([
		{ name: 'Bashir Sidi', number: '08103510117' },
		{ name: 'Ahmad Sidi', number: '08067753836' }
	]);
	const [ NewName, setNewName ] = useState('');

	const handleChange = (event) => {
		setNewName(event.target.value);
	};

	const addName = (event) => {
		event.preventDefault();
		if (NewName !== '') {
			var newPersons = [ ...Persons, { name: NewName } ];
			setPersons(newPersons);
		}

		setNewName('');
	};
	return (
		<div>
			<h1>Phonebook</h1>
			<form onSubmit={addName}>
				<div>
					Name: <input value={NewName} onChange={handleChange} />
					&nbsp;Number: <input value onChange />
					<button type="submit">Add</button>
				</div>
			</form>
			<h2>Numbers</h2>
			{console.log(Persons.map((person) => person.name))}
			{Persons.map(
				(person, i) =>
					person.name !== NewName ? (
						<div key={i}>{person.name}</div>
					) : (
						alert(`${NewName} is already added to Phonebook!`)
					)
			)}
		</div>
	);
};

export default App;
