import React from 'react';

const Header = ({ course }) => {
	return (
		<div>
			<h1>{course.name}</h1>
		</div>
	);
};

// const Total = ({ courses }) => {
// 	//const sum = course.parts.map((part) => part.exercises).reduce((a, b) => a + b);
// 	const sum = courses.map((course) =>
// 		course.parts.map((part) => part.exercises).reduce((a, b) => a + b)
// 	);
// 	return <p>Number of exercises {sum}</p>;
// };

const Part = (props) => {
	return (
		<p>
			{props.part.name} {props.part.exercises}
		</p>
	);
};

const Content = ({ courses }) => {
	return (
		<div>
			{courses.map((course) => course.parts.map((part, i) => <Part key={i} part={part} />))}
		</div>
	);
};

const Course = (props) => {
	return (
		<div>
			{props.courses.map((course, i) => (
				<div key={i}>
					<Header course={course} />
				</div>
			))}
			<Content courses={props.courses} />
			{/* <Total course={props.course} />  */}
		</div>
	);
};

const App = () => {
	const courses = [
		{
			name: 'Half Stack application development',
			parts: [
				{
					name: 'Fundamentals of React',
					exercises: 10
				},
				{
					name: 'Using props to pass data',
					exercises: 7
				},
				{
					name: 'State of a component',
					exercises: 14
				},
				{
					name: 'Redux',
					exercises: 11
				}
			]
		},
		{
			name: 'Node.js',
			id: 2,
			parts: [
				{
					name: 'Routing',
					exercises: 3,
					id: 1
				},
				{
					name: 'Middlewares',
					exercises: 7,
					id: 2
				}
			]
		}
	];

	return (
		<div>
			<Course courses={courses} />
		</div>
	);
};

export default App;
